import React, { useState, useEffect } from 'react';
import { Chart, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend } from 'chart.js';
import { Line } from 'react-chartjs-2';
import fetchSheetData from './api/googleSheets';

Chart.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

function Dashboard() {
   const [chartData, setChartData] = useState(null);

   const fetchData = async () => {
     const sheetData = await fetchSheetData();

     if (sheetData.length) {
       const labels = sheetData.map((item) => item[0]); // Assuming first column is Date or Time
       const values = sheetData.map((item) => parseFloat(item[1])); // Assuming second column is the sensor data
       
       const newChartData = {
         labels: labels,
         datasets: [
           {
             label: 'Sensor Data',
             data: values,
             fill: false,
             borderColor: 'rgba(75,192,192,1)',
             tension: 0.1,
           },
         ],
       };

       setChartData(newChartData); // Update the chart data
     }
   };

   useEffect(() => {
     fetchData(); // Fetch on initial render

     const intervalId = setInterval(fetchData, 5000); // Fetch data every 5 seconds

     return () => clearInterval(intervalId); // Clear interval when component unmounts
   }, []);

   return (
     <div className="dashboard-container">
       <div className="graph-container">
         {/* Left side - Graph */}
         {chartData ? (
           <Line data={chartData} />
         ) : (
           <p>Loading data...</p>
         )}
       </div>

       <div className="content-container">
         {/* Right side - Other content */}
         <h2>Other Content</h2>
         <p>Additional data, charts, or widgets can be placed here.</p>
       </div>
     </div>
   );
}

export default Dashboard;
