import React, { useState, useEffect } from 'react';
import { Chart, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend } from 'chart.js';
import { Line } from 'react-chartjs-2';
import fetchSheetData from './api/googleSheets';
import './Dashboard.css'; // Make sure your CSS matches the updated sections

// Register Chart.js modules
Chart.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

export default function Dashboard() {
  const [chartData, setChartData] = useState(null);
  const [prediction, setPrediction] = useState('');
  const [distance, setDistance] = useState(0.0);
  const [flowRate, setFlowRate] = useState(0.0);
  const [recentReadings, setRecentReadings] = useState([]); // Stores last 10 readings
  const [summaryStats, setSummaryStats] = useState({ avgDist: 0, maxFlow: 0, minFlow: 0 });

  // ‚úÖ Fetch ALL readings from Google Sheets
  const fetchData = async () => {
    const sheetData = await fetchSheetData();
    console.log("Raw sheet data:", sheetData);

    if (sheetData && sheetData.length) {
      const validRows = sheetData.filter(
        (item) =>
          item.length >= 2 &&
          !isNaN(parseFloat(item[0])) && // Distance
          !isNaN(parseFloat(item[1]))   // FlowRate
      );

      // Prepare chart data
      const labels = validRows.map((_, index) => `Reading ${index + 1}`);
      const distanceData = validRows.map((item) => parseFloat(item[0]));
      const flowrateData = validRows.map((item) => parseFloat(item[1]));

      setChartData({
        labels: labels,
        datasets: [
          { label: 'Distance (m)', data: distanceData, borderColor: '#4dabf7', tension: 0.3 },
          { label: 'Flowrate (m¬≥/s)', data: flowrateData, borderColor: '#ffa94d', tension: 0.3 },
        ],
      });

      // Store last 10 readings for the table
      setRecentReadings(validRows.slice(-10).reverse());

      // Calculate summary statistics
      const avgDist = (distanceData.reduce((a, b) => a + b, 0) / distanceData.length).toFixed(2);
      const maxFlow = Math.max(...flowrateData).toFixed(2);
      const minFlow = Math.min(...flowrateData).toFixed(2);

      setSummaryStats({ avgDist, maxFlow, minFlow });
    } else {
      setChartData(null);
      setRecentReadings([]);
    }
  };

  // ‚úÖ Fetch Prediction from Backend
  const fetchPrediction = async () => {
    try {
      const response = await fetch('http://127.0.0.1:8000/predict');
      const data = await response.json();
      setPrediction(data.Prediction);
      setDistance(data.Distance);
      setFlowRate(data.FlowRate);
    } catch (error) {
      console.error('Error fetching prediction:', error);
      setPrediction('Error fetching prediction');
    }
  };

  // ‚úÖ Chart.js options for dark mode (removing vertical lines)
  const chartOptions = {
    responsive: true,
    plugins: { legend: { labels: { color: '#E0E0E0' } } },
    scales: {
      x: { ticks: { color: '#E0E0E0' }, grid: { display: false } },
      y: { ticks: { color: '#E0E0E0' }, grid: { color: 'rgba(255, 255, 255, 0.1)' } }
    }
  };

  // ‚úÖ Generate Alert Messages
  const getAlertMessage = () => {
    if (prediction === 'Flood Risk' || flowRate > 0.8) {
      return { text: '‚ö†Ô∏è Flood Risk Detected! Initiate emergency protocol.', color: '#e63946' };
    }
    return { text: '‚úÖ All conditions normal. No action required.', color: '#2a9d8f' };
  };

  useEffect(() => {
    fetchData();
    fetchPrediction();
  }, []);

  return (
    <div>
      <h2 style={{ textAlign: 'center', marginTop: '20px', color: '#FFFFFF' }}>Flood Prediction Dashboard</h2>

      {/* Main Dashboard Layout */}
      <div className="dashboard-container">
        {/* Left Side - Graph */}
        <div className="graph-container">
          {chartData ? <Line data={chartData} options={chartOptions} /> : <p style={{ color: '#E0E0E0' }}>Loading data...</p>}
        </div>

        {/* Right Side - Readings & Prediction */}
        <div className="content-container">
          <div className="card">
            <h3>Latest Sensor Readings</h3>
            <p><strong>Distance:</strong> {distance} m</p>
            <p><strong>Flow Rate:</strong> {flowRate} m¬≥/s</p>
            <button onClick={fetchData}>Refresh Data</button>
          </div>

          <div className="card">
            <h3>Prediction</h3>
            <p style={{ fontWeight: 'bold', fontSize: '20px', color: prediction === 'Flood Risk' ? '#e63946' : '#2a9d8f' }}>
              {prediction === 'Flood Risk' ? '‚ö†Ô∏è ' : '‚úÖ '} {prediction}
            </p>
          </div>
        </div>
      </div>

      {/* üìà Recent Readings Table */}
      <div className="card full-width">
        <h3>üìä Recent Sensor Readings</h3>
        <div className="table-container">
          <table>
            <thead>
              <tr>
                <th>Timestamp</th>
                <th>Distance (m)</th>
                <th>Flow Rate (m¬≥/s)</th>
              </tr>
            </thead>
            <tbody>
              {recentReadings.map((row, index) => (
                <tr key={index}>
                  <td>{`Reading ${index + 1}`}</td>
                  <td>{row[0]}</td>
                  <td>{row[1]}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {/* üìä Summary Stats */}
      <div className="card full-width">
        <h3>üìä Summary of Last 50 Readings</h3>
        <p><strong>Average Distance:</strong> {summaryStats.avgDist} m</p>
        <p><strong>Max Flow Rate:</strong> {summaryStats.maxFlow} m¬≥/s</p>
        <p><strong>Min Flow Rate:</strong> {summaryStats.minFlow} m¬≥/s</p>
      </div>

      {/* üö® Alerts & Recommendations */}
      <div className="card full-width" style={{ backgroundColor: getAlertMessage().color }}>
        <h3>üö® Alerts & Recommendations</h3>
        <p style={{ fontWeight: 'bold', fontSize: '18px', color: '#FFF' }}>{getAlertMessage().text}</p>
      </div>
    </div>
  );
}
